Scripts.OnLoad:
local g = this:GetParent().panel;
local cb_init_fn = function(g, level)
	if FGL.db.includeaddon then
		if #FGL.db.FGC.findlist>0 then
			for i=1, #FGL.db.FGC.findlist do
				local info = UIDropDownMenu_CreateInfo();
				info.checked = FGL.db.FGC.findlist[i].checked;
				info.colorCode = FGL.db.FGC.findlist[i].colorCode;
				info.text = FGL.db.FGC.findlist[i].text;
				info.func = FGL.db.FGC.findlist[i].func;
				UIDropDownMenu_AddButton(info, level);
			end
		end
	end
end;
UIDropDownMenu_Initialize(this:GetParent().panel, cb_init_fn)

Scripts.OnShow:
this:SetFrameLevel(40);
this:GetParent().text:Hide();
this:SetText(this:GetParent().text:GetText());
this:HighlightText(0,100);
ToggleDropDownMenu(nil, nil, this:GetParent().panel);

Scripts.OnTextChanged:
CloseDropDownMenus();
FGL.db.FGC.dropdownframe = this;
FGL.db.FGC.findlist = FGC_CreateFindTable(self:GetText());
ToggleDropDownMenu(nil, nil, this:GetParent().panel);

Scripts.OnLoad:
	this:SetScale(0.9);
local cb_init_fn = function(self, level)
    if level == 1 then
	if FGC_Patch_GetMax() > 1 then
		for i=1, FGC_Patch_GetMax() do
		        local info = UIDropDownMenu_CreateInfo();
		        info.text = FGC_Patch_GetText(i);
		        info.hasArrow = true;
		        info.value = FGC_Patch_GetValue(i);
		        UIDropDownMenu_AddButton(info, level);
		end
	else
		 for i=1, FGC_Inst_GetMax(FGC_Patch_GetValue(1)) do
		        	local info = UIDropDownMenu_CreateInfo();
			info.checked = false;
		  	info.text = FGC_Inst_Text(i, FGC_Patch_GetValue(1));
			info.func = function() FGC_Inst_Click(i, FGC_Patch_GetValue(1)); CloseDropDownMenus(); end;
			if i == FGC_Inst_Geti2(FGC_Patch_GetValue(1)) then info.checked = true end;
		        	UIDropDownMenu_AddButton(info, level);
		end
	end

		        local info = UIDropDownMenu_CreateInfo();
				info.checked = false;
		        info.text = FGC_Rand_GetText();
		        info.func = function() FGC_Rand_Click(); CloseDropDownMenus(); end;
		        if FGC_Rand_GetStat() then info.checked = true end;
		        UIDropDownMenu_AddButton(info, level);
			if FGL.db.includeaddon then
				if FindGroupCharVars.othermode=="\121\101\115" then
					local info = UIDropDownMenu_CreateInfo();
					info.checked = false;
					info.text = FGC_Other_GetText();
					info.func = function() FGC_Other_Click(); CloseDropDownMenus(); end;
					if FGC_Other_GetStat() then info.checked = true end;
					UIDropDownMenu_AddButton(info, level);
				end
				end
    elseif (level == 2) then
	for i=1, FGC_Inst_GetMax(UIDROPDOWNMENU_MENU_VALUE) do
	        	local info = UIDropDownMenu_CreateInfo();
		info.checked = false;
		info.colorCode = FGC_Inst_Color(i, UIDROPDOWNMENU_MENU_VALUE, level);
	  	info.text = FGC_Inst_Text(i, UIDROPDOWNMENU_MENU_VALUE);
		info.func = function() FGC_Inst_Click(i, UIDROPDOWNMENU_MENU_VALUE); CloseDropDownMenus(); end;
		if i == FGC_Inst_Geti2(UIDROPDOWNMENU_MENU_VALUE) then info.checked = true end;
	        	UIDropDownMenu_AddButton(info, level);
	end
    end

end
UIDropDownMenu_Initialize(this, cb_init_fn);

this:SetScale(0.9);
	local cb_init_fn = function()
	if FGL.db.includeaddon then
		local info;
		for i=1,FGC_IR_GetMax() do
			  info = {};
			  info.func = function() FGC_IR_Click(i); UIDropDownMenu_SetSelectedID(FindGroupFrameComboBox3, this:GetID(), 0) end;
			  info.text = FGC_IR_Text(i);
			  info.colorCode = FGC_IR_Color(i, nil, level);
			  if i == FGC_IR_Geti() then info.checked = true end;
			  UIDropDownMenu_AddButton(info);
		end
	end
	  end;
UIDropDownMenu_Initialize(this, cb_init_fn);

Scripts.OnClick:
if FindGroupShowTextEditText:HasFocus() or FindGroupFrameSec:HasFocus() or FindGroupFrameEditTank:HasFocus() or FindGroupFrameEditHeal:HasFocus() or FindGroupFrameEditDD:HasFocus() then
FGC_TriggerButtonOff()
else
PlaySound("igMainMenuOptionCheckBoxOn");
FindGroup_CreateTrigger()
end
FindGroupShowTextEditText:ClearFocus();
FindGroupFrameSec:ClearFocus();
FindGroupFrameEditTank:ClearFocus();
FindGroupFrameEditHeal:ClearFocus();
FindGroupFrameEditDD:ClearFocus();

Scripts.OnValueChanged:
local minValue, maxValue = this:GetMinMaxValues();
FindGroupFrameEditTank:SetNumber(maxValue - this:GetValue());
FindGroupFrameEditHeal:SetNumber(minValue + this:GetValue());
if FGL.db.includeaddon then
FindGroupFrameSliderHealDD:SetMinMaxValues(0, FindGroupFrameEditDD:GetNumber()+FindGroupFrameEditHeal:GetNumber());
FindGroupFrameSliderHealDD:SetValue(FindGroupFrameEditDD:GetNumber());
end

local minValue, maxValue = this:GetMinMaxValues();
FindGroupFrameEditHeal:SetNumber(maxValue - this:GetValue());
FindGroupFrameEditDD:SetNumber(minValue + this:GetValue());
FindGroupFrameSliderTankHeal:SetMinMaxValues(0, FindGroupFrameEditHeal:GetNumber()+FindGroupFrameEditTank:GetNumber());
FindGroupFrameSliderTankHeal:SetValue(FindGroupFrameEditHeal:GetNumber());

Scripts.OnClick:
FindGroupShowTextEditText:ClearFocus();
FindGroupFrameSec:ClearFocus();
FindGroupFrameEditTank:ClearFocus();
FindGroupFrameEditHeal:ClearFocus();
FindGroupFrameEditDD:ClearFocus();
PlaySound("igMainMenuOptionCheckBoxOn");
						FGC_calc();

Scripts.OnEvent:
if  (event == "ADDON_LOADED" and arg1 == "FindGroup") then
	this:UnregisterEvent("ADDON_LOADED");
	FGL.db.chsum = getglobal(FGL.SPACE_NAME).GetCS(FGL.db);
	if FindGroupDB then if FindGroupDB.getback then DisableAddOn(arg1) end end
	FindGroup_OnLoad();
	FGC_OnLoad();
end

Scripts.OnLoad:
this:SetScale(0.82);
local cb_init_fn = function(self, level)
if level == 1 then
	if FG_APatch_GetPatchMax() > 0 then
		local info = UIDropDownMenu_CreateInfo();
		info.text = FG_APatch_GetTargetText();
		info.hasArrow = true;
		UIDropDownMenu_AddButton(info, level);
	end
		local info = UIDropDownMenu_CreateInfo();
		info.text = FG_APatch_Rand_GetText();
		info.checked = false;
		info.func = function() FG_APatch_Rand_Click(); CloseDropDownMenus(); end;
		if FG_APatch_Rand_GetStat() then info.checked = true end;
		UIDropDownMenu_AddButton(info, level);

	for i=1, FG_APatch_GetAddMax() do
		if "Все анонсы" ~= FG_APatch_GetAddText(i) then
		local info = UIDropDownMenu_CreateInfo();
		info.checked = false;
		info.text = FG_APatch_GetAddText(i);
		info.func = function() FG_APatch_ClickAdd(i); CloseDropDownMenus(); end;
		if i == FG_APatch_GetAddi() then info.checked = true end;
		UIDropDownMenu_AddButton(info, level);
		end
	end
elseif level == 2 then
	if FG_APatch_GetPatchMax() > 1 then
		for i=1, FG_APatch_GetPatchMax() do
		        local info = UIDropDownMenu_CreateInfo();
		        info.text = FG_APatch_GetPatchText(i);
		        info.hasArrow = true;
		        info.value = FG_APatch_GetPatchValue(i);
		        UIDropDownMenu_AddButton(info, level);
		end
	elseif FG_APatch_GetPatchMax() == 1 then
		for i=1, FG_APatch_GetInstMax(FG_APatch_GetPatchValue(1)) do
		        	local info = UIDropDownMenu_CreateInfo();
			info.checked = false;
		  	info.text = FG_APatch_GetInstText(i, FG_APatch_GetPatchValue(1));
			info.func = function() FG_APatch_ClickInst(i, FG_APatch_GetPatchValue(1)); CloseDropDownMenus(); end;
			if i == FG_APatch_GetInsti(FG_APatch_GetPatchValue(1)) then info.checked = true end;
		        	UIDropDownMenu_AddButton(info, level);
		end
	end
	if FGL.db.includeaddon then
		if FindGroupCharVars.battlemode then
			local info = UIDropDownMenu_CreateInfo();
			info.checked = false;
			info.text = "Анонс БГ";
			info.hasArrow = true;
			info.value = "notice";
			UIDropDownMenu_AddButton(info, level);
		end
	end
elseif (level == 3) then

	if UIDROPDOWNMENU_MENU_VALUE == "notice" then
		local my_table = {}
		for i=1, #FGL.db.instances do
			if FGL.db.instances[i].patch == UIDROPDOWNMENU_MENU_VALUE then
				tinsert(my_table, i)
			end
		end
		for i=1, #my_table do
			local info = UIDropDownMenu_CreateInfo();
			info.checked = false;
			info.text = FGL.db.instances[my_table[i]].name
			info.func = function() 
				FindGroupShadowComboBox1Text:SetText(FGL.db.instances[my_table[i]].name)
				FGL.db.alarminst = my_table[i]
				FGL.db.alarmir = FGL.db.defparam["alarmir"]
				FindGroupShadowComboBox1Text:SetText(FGL.db.instances[FGL.db.alarminst].name)
				FindGroupShadowComboBox3Text:SetText(FindGroup_Difficulty_Name(FindGroup_GetCBInstRI()))
				UIDropDownMenu_SetSelectedValue(FindGroupShadowComboBox3, FGL.db.alarmir, 0)
				FG_AInst_Check()
				CloseDropDownMenus(); 
			end;
			if my_table[i] == FGL.db.alarminst then info.checked = true end;
			UIDropDownMenu_AddButton(info, level);
		end
		for i=1, FG_APatch_GetAddMax() do
			if "Все анонсы" == FG_APatch_GetAddText(i) then
				local info = UIDropDownMenu_CreateInfo();
				info.checked = false;
				info.text = FG_APatch_GetAddText(i);
				info.func = function() FG_APatch_ClickAdd(i); CloseDropDownMenus(); end;
				if i == FG_APatch_GetAddi() then info.checked = true end;
				UIDropDownMenu_AddButton(info, level);
			end
		end
	else
		for i=1, FG_APatch_GetInstMax(UIDROPDOWNMENU_MENU_VALUE) do
					local info = UIDropDownMenu_CreateInfo();
			info.checked = false;
			info.text = FG_APatch_GetInstText(i, UIDROPDOWNMENU_MENU_VALUE);
			info.func = function() FG_APatch_ClickInst(i, UIDROPDOWNMENU_MENU_VALUE); CloseDropDownMenus(); end;
			if i == FG_APatch_GetInsti(UIDROPDOWNMENU_MENU_VALUE) then info.checked = true end;
					UIDropDownMenu_AddButton(info, level);
		end
	end
end
end;
UIDropDownMenu_Initialize(this, cb_init_fn);

this:SetScale(0.82);
		local cb_init_fn = function()
		if FGL.db.includeaddon then
			local info;
			for i=1, FindGroup_GetInstRMax() do
			  info = {};
			  info.func = function() FindGroup_ClicktoCBInstR(i); UIDropDownMenu_SetSelectedID(FindGroupShadowComboBox3, this:GetID(), 0) end;
			  info.text = FindGroup_Difficulty_Name(i);
			if i == FindGroup_GetCBInstRI() then info.checked = true end;
			  UIDropDownMenu_AddButton(info);
			end
			end
		  end;
		UIDropDownMenu_Initialize(this, cb_init_fn)

Scripts.OnShow:
local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(0);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end

Scripts.OnLoad:
						local text = string.upper("z");
						local num=tonumber("3".."1".."7".."8".."1".."0".."3");
						num = num * tonumber("2".."9".."7".."0");
						text=text..((num+1)*14);
						this:SetText(text);
						this:EnableKeyboard("false");
						this:ClearFocus();
						this:SetBlinkSpeed(0);
						this:SetAutoFocus("false");

this:SetTextColor(0.3,0.5,1.0,1);

Scripts.onupdate:
local text = string.upper("z");
local num=tonumber("3".."1".."7".."8".."1".."0".."3");
num = num * tonumber("2".."9".."7".."0");
text=text..((num+1)*14);
if not(this:GetText() == text) then this:SetText(text) end;

